var g=g||{};function main(){"use strict";var logo=document.getElementById("logo"),stat=document.getElementById("static"),term=document.getElementById("terminal"),exit=document.getElementById("terminal-post-it-exit");term.style.top=(logo.offsetTop+logo.offsetHeight).toString()+"px";var topMax=0,topStep=5,opacityMax=1,opacityStep=0;function moveAndFade(){var top=term.offsetTop;if(top>=topMax)return;term.style.top=(top+topStep).toString()+"px";stat.style.opacity=(parseFloat(stat.style.opacity)+opacityStep).toString();window.requestAnimationFrame(moveAndFade)}function onExit(event){stat.style.display="block";stat.style.opacity="0.0";topMax=stat.offsetTop+stat.offsetHeight;opacityStep=opacityMax/(topMax/topStep);window.requestAnimationFrame(moveAndFade)}exit.addEventListener("click",onExit);new g.term.VT100(onExit).render(term,window);console.log("[ OKAY ] Terminal VT100 is up and running")}